import React from 'react';
import { Tween } from 'react-gsap';

import {
    Button,
    Row
} from 'react-bootstrap';

export default class Games extends React.Component {
    constructor(props) {
        super(props);
        this.state = { backgroundImage: this.props.buttons[0].backgroundImage };
    }

    changeBG = (backgroundImage) => {
        if (this.state.backgroundImage !== backgroundImage) {
            this.setState({ backgroundImage: backgroundImage });
            const tween = this.tween.getGSAP();
            tween.seek(0);
        }
    }

    componentDidMount() {
        this.props.buttons.forEach((picture) => {
            const img = new Image();
            img.src = picture.backgroundImage;
        });
    }

	render() {
        const buttons = this.props.buttons;
		return (
            <section className="games text-white ml-4 mr-4 pl-5 pr-5 pt-4 pb-2" >
            <Tween ref={ref => this.tween = ref} duration={0.5} from={{ autoAlpha: 0 }} >
                <div className="gamesBG" style={{backgroundImage: "url(" + this.state.backgroundImage + ")"}} />
            </Tween>
            <Row className="justify-content-around">
                <div className="gamesContent">
                    <h1 className="shadow-black">Games</h1>
                    <p className="shadow-black">ActionScript, JavaScript (Phaser), Unity (C#).</p>
                    {
                        buttons && buttons.map((value, index) => { return(
                            <p key={index}><Button href={value.href} target="_blank" onMouseOver={(e) => this.changeBG(value.backgroundImage)} >{value.label}</Button></p>
                        )})
                    }
                </div>
                <div className="d-none d-sm-block justify-content-end">Wea</div>
            </Row>
            </section>
		)
	}
}
