import React from 'react';
import GoogleMap from './GoogleMap';

export default class Intro extends React.Component {
    constructor(props) {
        super(props);
        this.mapContainer = React.createRef();
    }

    apiKey = 'AIzaSyBlWs_p76IGB3rrCWIIpUkEm01tWMul2S4';
    
    render() {
        const mapStyles = {
            width: '80vw',
            height: '80vh',
            maxWidth: '400px',
            maxHeight: '300px'
        };
		return (
			<section fluid="true" className="intro text-light ml-4 mr-4 pl-4 pr-4 pt-3 pb-1" >
                <h1>Greetings, good citizen!</h1>
                <p>Here you'll find some samples of my work as both a Developer and a Designer, which includes motion graphics and video editing, photo retouching and visual effects, banners, Web games, and even my regular CV.</p>
                <p>Thanks for stopping by!</p>
                <div ref={this.mapContainer} style={mapStyles}>
                    <GoogleMap id="map" width={this.mapContainer.width} maxWidth={mapStyles.maxWidth} height={this.mapContainer.height} maxHeight={mapStyles.maxHeight}></GoogleMap>
                </div>
            </section>
		)
	}
}
