import React from 'react';
import certificate from '../images/bitdegree-bootstrap.png';

import {
	Nav,
    Image,
    Row
} from 'react-bootstrap';

export default class Footer extends React.Component {

	render() {
        const navLinks = this.props.nav;
        return (
            <footer className="text-white-50 p-3 pr-4">
                <Nav className="justify-content-end">
                    <p className="mt-auto mb-auto">©2019, Daniel Ramírez</p>
                    { navLinks && navLinks.map((value, index) => { return (
                        <Nav.Link className="nav-link p-0 ml-3" href={value.href} alt={value.item} target="_blank" key={index} >
                            <Image src={value.icon} height={25} ></Image>
                        </Nav.Link>
                    )})}
                </Nav>
                <Row className="ml-3 mr-3 mt-2">
                    <a href="https://www.testdome.com/cert/3f221a5f192b42aea4aee1e71a7c70fb" className="testdome-certificate-stamp silver mr-3" target="_blank" rel="noopener noreferrer"><span className="testdome-certificate-name">Daniel Ramírez</span><span className="testdome-certificate-test-name">HTML/CSS and React </span><span className="testdome-certificate-card-logo">TestDome<br />Certificate</span></a>
                    <a href="https://www.testdome.com/cert/a412f412c4e5441ebaf3a93fd31f9275" className="testdome-certificate-stamp silver mr-3" target="_blank" rel="noopener noreferrer"><span className="testdome-certificate-name">Daniel Ramírez</span><span className="testdome-certificate-test-name">HTML/CSS, JavaScript, and React </span><span className="testdome-certificate-card-logo">TestDome<br />Certificate</span></a>
                    <a href="https://www.bitdegree.org/api/certificate?slug=bootstrap-tutorial&action=attachment&username=daniel-ramirez-rodriguez373709" className="certificate" target="_blank" rel="noopener noreferrer"><img src={certificate} alt="Bootstrap 4 Certificate" width={175} height="auto" ></img></a>
                </Row>
            </footer>
        )
	}
}
