import React from 'react';

import {
	Row,
	Navbar,
	Nav,
	Image
} from 'react-bootstrap';

export default class Footer extends React.Component {

	render() {
        const navLinks = this.props.nav;
        return (
            <footer className="text-white-50 text-right m-auto sticky-bottom">
                <Row className="ml-3 text-right align-bottom">
                    <p className="mt-auto mb-auto">©2019, Daniel Ramírez</p>
                    <Nav>
                        { navLinks && navLinks.map((value, index) => { return (
                            <Nav.Link className="nav-link text-right align-self-end m-0" href={value.href} alt={value.item} >
                                <Image src={value.icon} height={25} className="p-0" ></Image>
                            </Nav.Link>
                        )})}
                    </Nav>
                </Row>
            </footer>
        )
		/*const navLinks = this.props.nav;
		return (
			<header className="sticky-top">
				<Navbar  variant="dark" expand="sm">
					<Navbar.Brand href="/" >
						<Row>
							<Col xs={3} ><Logo /></Col>
							<Col xs={9} >Daniel Ramírez</Col>
						</Row>
					</Navbar.Brand>
					<Navbar.Toggle aria-controls="basic-navbar-nav" />
					<Navbar.Collapse className="justify-content-end">
						<Nav>
							{ navLinks && navLinks.map((value, index) => { return (
								<Nav.Link key={index} className="nav-link text-right" href={value.href} >{value.item}</Nav.Link>
							)})}
						</Nav>
					</Navbar.Collapse>
				</Navbar>
			</header>
		)*/
	}
}
