import React from 'react';
import Iframe from 'react-iframe';

export default class BannerCollection extends React.Component {
	render() {
        const banners = this.props.banners;
		return (
			<section fluid="true" className="intro text-light ml-4 mr-4 pl-4 pr-4 pt-3 pb-1" >
                <h1>Animated Banners</h1>
                <p>HTML5 banners for desktop, animated with GSAP.</p>
                {
                    banners && banners.map((value, index) => { return(
                        <Iframe title="ArtMatch" url={value.href} width={value.width} height={value.height} frameBorder="0" scrolling="no" />
                    )})
                }
                <Iframe title="ArtMatch" url="http://dallenad.com/banners/ArtMatch/artmatch_728x90.html" width={728} height="90px" frameBorder="0" scrolling="no" />
            </section>
		)
	}
}
