import React from 'react';

import {
    Row,
    Link,
	Button,
	Container
} from 'react-bootstrap';

export default class Games extends React.Component {
    constructor(props) {
        super(props);
    }

	render() {
        const buttons = this.props.buttons;
		return (
			<section className="games text-white ml-4 mr-4 pl-5 pr-5 pt-4 pb-1">
                <h1 className="shadow-black">Games</h1>
                <p className="shadow-black">ActionScript, JavaScript (Phaser), Unity (C#).</p>
                {
                    buttons && buttons.map((value, index) => { return(
                        <p key={index}><Button href={value.href} target="_blank" onMouseOver={(e) => console.log(index)} >{value.label}</Button></p>
                    )})
                }
            </section>
		)
	}
}
