import React from 'react';
import { Tween, Timeline } from 'react-gsap';

import {
	Button
} from 'react-bootstrap';

export default class Games extends React.Component {
    constructor(props) {
        super(props);
        this.state = { backgroundImage: "url(https://scontent.fsjo1-1.fna.fbcdn.net/v/t31.0-8/11045482_10206130459367808_6116952346692362417_o.jpg?_nc_cat=108&_nc_ht=scontent.fsjo1-1.fna&oh=d91ab80b9c919b6e3ea39fa4a2fabfc1&oe=5D6406DC)" };
        this.gamesSection = React.createRef();
    }

    componentDidMount() {
        //this.setState();
    }

    changeBG = (backgroundImage) => {
        this.setState({ backgroundImage: backgroundImage });
        //this.gamesSection.props.playState = "stop";
        console.log(this.gamesSection.current);
    }

	render() {
        const buttons = this.props.buttons;
		return (
			<Tween ref={this.gamesSection} duration={0.5} from={{ autoAlpha: 0 }} playState="play">
            <section className="games text-white ml-4 mr-4 pl-5 pr-5 pt-4 pb-1" style={this.state} >
                <h1 className="shadow-black">Games</h1>
                <p className="shadow-black">ActionScript, JavaScript (Phaser), Unity (C#).</p>
                {
                    buttons && buttons.map((value, index) => { return(
                        <p key={index}><Button href={value.href} target="_blank" onMouseOver={(e) => this.changeBG(value.backgroundImage)} >{value.label}</Button></p>
                    )})
                }
            </section>
            </Tween>
		)
	}
}
