import React from 'react';

export default class Intro extends React.Component {
	render() {
        //let data = this.props.data;
		return (
			<section fluid="true" className="intro text-light ml-4 mr-4 pl-4 pr-4 pt-3 pb-1" >
                <h1>Greetings, good citizen!</h1>
                <p>Here you'll find some samples of my work as both a Developer and a Designer, which includes motion graphics and video editing, photo retouching and visual effects, banners, Web games, and even my regular CV.</p>
                <p>Thanks for stopping by!</p>
                <h3>My Google Maps Demo</h3>
                <div id="map" style={{
                    height: '400px',  /* The height is 400 pixels */
                    width: '100%'  /* The width is the width of the web page */
                }}></div>
                <script>
                    {function initMap() {
                    // The location of Uluru
                    var uluru = {lat: -25.344, lng: 131.036};
                    // The map, centered at Uluru
                    var map = new google.maps.Map(
                        document.getElementById('map'), {zoom: 4, center: uluru});
                    // The marker, positioned at Uluru
                    var marker = new google.maps.Marker({position: uluru, map: map});
                    }}
                </script>
                <script async defer src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBlWs_p76IGB3rrCWIIpUkEm01tWMul2S4&callback=initMap">
                </script>
            </section>
		)
	}
}
